
CREATE TABLE civilizations(
    id                   INTEGER  NOT NULL PRIMARY KEY 
   ,name                 VARCHAR(10) NOT NULL
   ,expansion            VARCHAR(17) NOT NULL
   ,army_type            VARCHAR(30) NOT NULL
   ,team_bonus           VARCHAR(93) NOT NULL
   ,civilization_bonus_0 VARCHAR(118) NOT NULL
   ,civilization_bonus_1 VARCHAR(109) NOT NULL
   ,civilization_bonus_2 VARCHAR(98)
   ,civilization_bonus_3 VARCHAR(78)
   ,civilization_bonus_4 VARCHAR(48)
 );
